sys_script_include.script:
var dsuUtilities = Class.create();
dsuUtilities.prototype = {
	initialize: function() {
	},

	updateRecord: function(table,enq,str){
		var gr = new GlideRecord(table);
		gr.addEncodedQuery(enq);
		gr.query();
		while(gr.next()){
			for(var i = 0; i < str.fields.length; i++){
				gr.setValue(str.fields[i],str.values[i]);
			}
			gr.update();
		}
	},

	insertRecord: function(table,array){
		var gr = new GlideRecord(table);
		gr.initiaize();
		for(var key in array){
			gr.setValue(key.toString(),array[key].toString());
		}
		gr.insert();
	},

	countRecord: function(table,enq){
		var gr = new GlideAggregate(table);
		gr.addAggregate('COUNT');
		gr.addEncodedQuery(enq);
		gr.query();
		var count = 0;
		if(gr.next()){
			count = gr.getAggregate('COUNT');
		}
		return count;
	},

	getRecord: function(table,enq,need){
		var send = '';
		var gr = new GlideRecord(table);
		gr.addEncodedQuery(enq);
		gr.query();
		while(gr.next()){
			send = gr.getElement(need);
		}
		return send;
	},

	type: 'dsuUtilities'
};
