var domain_type = "Customer"; //set Customer as default
var customer_value = false; //set false as default
var manufacturer_value = false; //set false as default
var vendor_value = false; //set false as default
if(current.variables.sb_company_type == "customer"){
	customer_value = true;
	domain_type = "Customer";
}
if(current.variables.sb_company_type == "manufacturer"){
	manufacturer_value = true;
	domain_type = "Vendor";
}
if(current.variables.sb_company_type == "vendor"){
	vendor_value = true;
	domain_type = "Vendor";
}

